import Image from "../nillkin-case.webp";
import { Link } from "react-router-dom";
import logo from  '../landing/banner-0.jpg'
function FeatureProduct(props) {
  return (
    <div className="col">
      <div className="card shadow-sm">
        <img
          className="card-img-top bg-dark cover"
          height="250"
          alt=""
          src={props.product.tumbrImage}
          style={{aspectRatio:'1/1'}}
        />
        <div className="card-body">
          <h5 className="card-title text-center">{props.product.name}</h5>
          <p className="card-text text-center text-muted"></p>
          <div className="d-grid gap-2">
            {/* <Link to={`/products/${props.product._id}`} state={{tumImage:props.product.tumbrImage}}  className="btn btn-outline-dark" replace>
              İncele
            </Link> */}
            <Link
            to={{
              pathname:`/producuts/${props.product._id}`,
              state:{tumImage:props.product.tumbrImage},
            }}
            >İncele</Link>
          </div>
        </div>
      </div>
    </div>
  );
}

export default FeatureProduct;
